and ( sub.h_tenant_id = #{userOrganizationId} or sub.created_by_tenant_id = #{userOrganizationId} or sub.id in )
)
and sub.is_enabled = #{isEnabled}
) else 0 end AS children_num, opp.enable_role_inherit, opp.enable_role_allocate, case when opp.enable_role_permission = 1 then 1 when opp.enable_role_permission = 0 and ( ir.code in (#{siteSuperRole}, #{tenantSuperRole}) or exists ( select 1 from hiam_label_rel hlr join iam_label il on (hlr.label_id = il.id) where hlr.data_id = ir.id and hlr.data_type = 'ROLE' and hlr.assign_type = 'A' and il.name = #{tenantAdminLabel} ) ) then 1 else 0 end as enable_role_permission from iam_role ir join iam_role_tl irt on (ir.id = irt.id and irt.lang = #{lang}) join hpfm_tenant ht on ht.tenant_id = ir.h_tenant_id left join hpfm_tenant_tl httl ON ht.tenant_id = httl.tenant_id AND httl.lang = #{lang} left join iam_role iri on iri.id = ir.h_inherit_role_id left join iam_role_tl irit on (irit.id = iri.id and irit.lang = #{lang}) left join hpfm_tenant_tl htti ON htti.tenant_id = iri.h_tenant_id AND htti.lang = #{lang} left join iam_role irp on irp.id = ir.h_parent_role_id left join iam_role_tl irpt on (irpt.id = irp.id and irpt.lang = #{lang}) left join hpfm_tenant_tl http ON http.tenant_id = irp.h_tenant_id AND http.lang = #{lang} left join oauth_password_policy opp on (opp.organization_id = ir.h_tenant_id) where ir.is_enabled = 1 and ( ir.id in
or ir.id in
#{queryRoleId}
)
and ir.fd_level = #{level}
having ( children_num > 0 or (
and ir.is_enabled = #{isEnabled}
and 1=1
) ) order by ir.h_tenant_id, ir.h_level_path

select ir.id, ir.code as code, irt.name as name, ir.description, ir.fd_level, ir.h_tenant_id, ir.h_parent_role_id, ir.h_inherit_role_id, ir.is_enabled, case when ir.is_built_in = 1 then 'default' else 'custom' end as role_source, ir.h_level_path, ir.object_version_number, ir.created_by, ht.tenant_num, httl.tenant_name, irit.name as inherited_role_name, htti.tenant_name as inherited_role_tenant_name, irpt.name as parent_role_name, http.tenant_name as parent_role_tenant_name, ( select count(*) from iam_role sub join iam_role_tl subt on sub.id = subt.id and subt.lang =#{lang} join hpfm_tenant ht on sub.h_tenant_id = ht.tenant_id where sub.fd_level = ir.fd_level and ( sub.h_level_path like concat(ir.h_level_path, '|%')
and ( sub.h_tenant_id = #{userOrganizationId} or sub.created_by_tenant_id = #{userOrganizationId} or sub.id in )
)
and sub.is_enabled = #{isEnabled}
) as children_num from iam_role ir join iam_role_tl irt on (ir.id = irt.id and irt.lang = #{lang}) join hpfm_tenant ht on ht.tenant_id = ir.h_tenant_id left join hpfm_tenant_tl httl ON ht.tenant_id = httl.tenant_id AND httl.lang = #{lang} left join iam_role iri on iri.id = ir.h_inherit_role_id left join iam_role_tl irit on (irit.id = iri.id and irit.lang = #{lang}) left join hpfm_tenant_tl htti ON htti.tenant_id = iri.h_tenant_id AND htti.lang = #{lang} left join iam_role irp on irp.id = ir.h_parent_role_id left join iam_role_tl irpt on (irpt.id = irp.id and irpt.lang = #{lang}) left join hpfm_tenant_tl http ON http.tenant_id = irp.h_tenant_id AND http.lang = #{lang} where ir.id in
#{item}
and ir.fd_level = #{level}
having ( children_num > 0 or (
and ir.is_enabled = #{isEnabled}
and 1=1
) ) order by ir.h_tenant_id, ir.h_level_path

select ir.id, ir.h_level_path from iam_role ir join iam_role irp on (irp.id = #{parentRoleId}) where ( (ir.h_parent_role_id = #{parentRoleId} or ir.h_level_path like concat(irp.h_level_path, '|%'))
and ( ir.h_tenant_id = #{userOrganizationId} or ir.created_by_tenant_id = #{userOrganizationId} or ir.id in )
) order by ir.h_tenant_id, ir.h_level_path

SELECT sub_ir.id as role_id, imr.member_id as member_id FROM iam_member_role imr, iam_role sub_ir WHERE imr.member_id IN
#{item}
AND imr.member_type = 'user' AND imr.role_id = sub_ir.id AND sub_ir.is_enabled = 1 AND sub_ir.id NOT IN ( SELECT sub_ir2.id FROM iam_member_role imr2, iam_role ir_admin2, iam_role sub_ir2 left join oauth_password_policy opp on (sub_ir2.h_tenant_id = opp.organization_id) WHERE imr2.member_id = #{userId} AND imr2.member_type = 'user' AND ( ( imr2.role_id IN ( SELECT irp.role_id FROM iam_menu ps, iam_role_permission irp WHERE (irp.h_create_flag = 'Y' OR irp.h_inherit_flag = 'Y') AND (irp.permission_id = ps.id AND irp.type = 'PS') AND ps.type = 'ps' AND ps.h_enabled_flag = 1 AND ps.code LIKE #{adminRolePs} ) OR ir_admin2.code in (#{siteSuperRole}, #{tenantSuperRole}) ) AND sub_ir2.h_level_path LIKE concat(ir_admin2.h_level_path, '%')
AND ( sub_ir2.created_by_tenant_id = #{userOrganizationId} OR sub_ir2.h_tenant_id = #{userOrganizationId}
OR sub_ir2.id IN ( SELECT imr2.role_id FROM iam_member_role imr2 WHERE imr2.member_id = #{userId} AND imr2.member_type = 'user' AND ir_admin2.is_enabled = 1 )
)
AND ir_admin2.is_enabled = 1 ) AND ( imr2.role_id != sub_ir2.id
OR (opp.enable_role_allocate = 1 or opp.enable_role_allocate is null)
) AND imr2.role_id = ir_admin2.id AND sub_ir2.h_tenant_id = opp.organization_id AND sub_ir2.is_enabled = 1 )
