mapper.select:
select hts.temp_server_id, hts.tenant_id, ht.tenant_name, hts.message_code, htst.message_name, hts.enabled_flag, hts.object_version_number from hmsg_template_server hts join hmsg_template_server_tl htst on htst.temp_server_id = hts.temp_server_id and htst.lang = #{lang} join hpfm_tenant_tl ht on ht.tenant_id = hts.tenant_id and ht.lang = #{lang}
and hts.tenant_id IN (#{tenantId} , 0)
AND hts.message_code LIKE #{messageCodeLike}
AND htst.message_name LIKE #{messageNameLike}
