mapper.select:
(SELECT
FROM hpfm_dashboard_card hdc JOIN hpfm_dashboard_role_card hdrc ON ( hdc.id = hdrc.card_id AND hdrc.role_id IN
#{roleId}
,( SELECT ir1.id FROM iam_role ir JOIN iam_role ir1 ON ir.h_inherit_role_id = ir1.id AND ir1.code IN
#{item}
WHERE ir.id IN
#{roleId}
)
) ) LEFT JOIN hpfm_dashboard_card_tl hdct ON hdc.id=hdct.id AND hdct.lang=#{lang} WHERE hdc.enabled_flag = 1) UNION (SELECT
FROM iam_role_permission irp INNER JOIN hiam_sec_grp hsg ON irp.type = 'SG' AND irp.permission_id = hsg.sec_grp_id AND irp.role_id IN
#{roleId}
,( SELECT ir1.id FROM iam_role ir JOIN iam_role ir1 ON ir.h_inherit_role_id = ir1.id AND ir1.code IN
#{item}
WHERE ir.id IN
#{roleId}
)
) INNER JOIN hiam_sec_grp_acl_dashboard hsgad ON hsg.sec_grp_id = hsgad.sec_grp_id INNER JOIN hpfm_dashboard_card hdc ON hsgad.card_id = hdc.id AND hdc.enabled_flag = 1 LEFT JOIN hpfm_dashboard_card_tl hdct ON hdc.id = hdct.id AND hdct.lang = #{lang})

SELECT hdc.code, hdl.card_id, hdct.name, hdc.card_params, hdl.w, hdl.h, hdl.x, hdl.y, 0 init_flag FROM hpfm_dashboard_layout hdl join hpfm_dashboard_card hdc on (hdc.id=hdl.card_id AND hdc.enabled_flag=1) left join hpfm_dashboard_card_tl hdct ON hdc.id = hdct.id and hdct.lang = #{lang} WHERE 1=1 AND hdl.user_id = #{userId} AND hdl.role_id IN
#{item}
AND hdl.tenant_id = #{tenantId}

(SELECT
FROM hpfm_dashboard_card hdc LEFT JOIN hpfm_dashboard_role_card hdrc ON ( hdc.id = hdrc.card_id AND hdrc.role_id IN
#{roleId}
) LEFT JOIN hpfm_dashboard_card_tl hdct ON hdc.id=hdct.id AND hdct.lang=#{lang} WHERE hdc.enabled_flag = 1) UNION (SELECT
FROM iam_role_permission irp INNER JOIN hiam_sec_grp hsg ON irp.type = 'SG' AND irp.permission_id = hsg.sec_grp_id AND irp.role_id IN
#{roleId}
INNER JOIN hiam_sec_grp_acl_dashboard hsgad ON hsg.sec_grp_id = hsgad.sec_grp_id INNER JOIN hpfm_dashboard_card hdc ON hsgad.card_id = hdc.id AND hdc.enabled_flag = 1 LEFT JOIN hpfm_dashboard_card_tl hdct ON hdc.id = hdct.id AND hdct.lang = #{lang})
