mapper.select:
SELECT sql_value FROM hpfm_permission_rule WHERE enabled_flag = 1
AND rule_id IN
#{item.ruleId}

SELECT hpr.rule_id, hpr.rule_code, hprt.rule_name, hpr.description, hpr.sql_value, hpr.tenant_id, hpr.enabled_flag, hpr.object_version_number, hpr.rule_type_code, hpr.editable_flag, httl.tenant_name FROM hpfm_permission_rule hpr left join hpfm_permission_rule_tl hprt on hprt.rule_id = hpr.rule_id and hprt.lang = #{lang} join hpfm_tenant ht on ht.tenant_id = hpr.tenant_id LEFT JOIN hpfm_tenant_tl httl ON ht.tenant_id = httl.tenant_id AND httl.lang = #{lang} WHERE 1=1
AND hpr.tenant_id = #{tenantId}
AND hpr.rule_code LIKE #{codeLike}
AND hprt.rule_name LIKE #{nameLike}
AND hpr.enabled_flag = #{enabledFlag}
AND hpr.description LIKE #{descriptionLike}

SELECT hpr.rule_id, hpr.rule_code, hprt.rule_name, hpr.description, hpr.sql_value, hpr.tenant_id, hpr.enabled_flag, hpr.object_version_number, hpr.rule_type_code, hpr.editable_flag, httl.tenant_name FROM hpfm_permission_rule hpr left join hpfm_permission_rule_tl hprt on hprt.rule_id = hpr.rule_id and hprt.lang = #{lang} join hpfm_tenant ht on ht.tenant_id = hpr.tenant_id LEFT JOIN hpfm_tenant_tl httl ON ht.tenant_id = httl.tenant_id AND httl.lang = #{lang} WHERE 1=1 AND (hpr.tenant_id = #{tenantId} OR hpr.tenant_id = 0)
AND hpr.rule_code LIKE #{codeLike}
AND hprt.rule_name LIKE #{nameLike}
AND hpr.enabled_flag = #{enabledFlag}
AND hpr.description LIKE #{descriptionLike}

SELECT hpr.rule_id, hpr.rule_code, hprt.rule_name, hpr.description, hpr.sql_value, hpr.tenant_id, hpr.enabled_flag, hpr.editable_flag, hpr.object_version_number, hpr.rule_type_code, httl.tenant_name FROM hpfm_permission_rule hpr left join hpfm_permission_rule_tl hprt on hprt.rule_id = hpr.rule_id and hprt.lang = #{lang} join hpfm_tenant ht on ht.tenant_id = hpr.tenant_id LEFT JOIN hpfm_tenant_tl httl ON ht.tenant_id = httl.tenant_id AND httl.lang = #{lang} WHERE 1=1 AND (hpr.tenant_id = #{tenantId} OR hpr.tenant_id = 0)
AND hpr.rule_code LIKE #{codeLike}
AND hprt.rule_name LIKE #{nameLike}
AND hpr.enabled_flag = 1

SELECT hpru.rule_id, hpru.rule_code, hpru.enabled_flag, hpru.tenant_id, hpru.object_version_number FROM hpfm_permission_rule hpru WHERE hpru.rule_id IN
#{ruleId}
AND NOT EXISTS( SELECT 1 FROM hpfm_permission_rel hpre WHERE hpre.rule_id = hpru.rule_id )
