mapper.select:
SELECT hcr.request_id, hcr.concurrent_id, hc.conc_code, hct.conc_name, hcr.cycle_flag, hcr.request_param, hcr.start_date, hcr.end_date, hcr.job_id, hcr.job_id task_id, hcr.tenant_id, ht.tenant_name, hji.parent_id, iu.real_name username, qt.TRIGGER_STATE job_status, hcr.object_version_number FROM hsdr_conc_request hcr JOIN hsdr_concurrent hc ON hc.concurrent_id = hcr.concurrent_id LEFT JOIN hpfm_tenant_tl ht ON ht.tenant_id = hcr.tenant_id AND ht.lang = #{lang} LEFT JOIN hsdr_concurrent_tl hct ON hc.concurrent_id = hct.concurrent_id AND hct.lang = #{lang} JOIN iam_user iu ON iu.id = hcr.created_by JOIN hsdr_job_info hji ON hcr.job_id = hji.job_id LEFT JOIN qrtz_triggers qt ON qt.TRIGGER_NAME = CONCAT('',hcr.job_id) AND qt.TRIGGER_GROUP = CONCAT('',hcr.tenant_id) WHERE 1 = 1
AND hcr.tenant_id = #{tenantId}
AND hcr.cycle_flag = #{cycleFlag}
AND hcr.job_id = #{jobId}
AND hcr.job_id = #{taskId}
AND hc.conc_code LIKE #{concCodeLike}
AND hct.conc_name LIKE #{concNameLike}
AND qt.TRIGGER_STATE IN
#{item}
OR qt.TRIGGER_STATE IS null
EXISTS ( SELECT 1 FROM hsdr_job_log hjl WHERE hjl.job_id = hcr.job_id AND hjl.client_result = #{clientResult} )
